import { MeetingConfig, ScoreData } from '../types';

// Mock API service for development
class MockApiService {
  private mockScoreData: { [botId: string]: ScoreData } = {};

  async configureBot(botId: string, config: MeetingConfig): Promise<any> {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Store config for later use
    this.mockScoreData[botId] = {
      botId,
      config,
      scores: {
        participation: Math.floor(Math.random() * 100),
        engagement: Math.floor(Math.random() * 100),
        clarity: Math.floor(Math.random() * 100),
        efficiency: Math.floor(Math.random() * 100)
      },
      summary: {
        totalParticipants: Math.floor(Math.random() * 20) + 5,
        meetingDuration: Math.floor(Math.random() * 60) + 30,
        topicsDiscussed: Math.floor(Math.random() * 10) + 3
      },
      timestamp: new Date().toISOString()
    };

    return { success: true, botId };
  }

  async getScoreData(botId: string): Promise<ScoreData> {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 800));

    if (!this.mockScoreData[botId]) {
      throw new Error('Score data not found');
    }

    return this.mockScoreData[botId];
  }

  async getBots(): Promise<any[]> {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 500));

    return Object.keys(this.mockScoreData).map(botId => ({
      id: botId,
      name: `Bot ${botId}`,
      status: 'completed' as const,
      config: this.mockScoreData[botId].config,
      scoreData: this.mockScoreData[botId],
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    }));
  }
}

// Create singleton instance
const apiService = new MockApiService();

export default apiService;
